<div class="liste grid col-lg-12 p-0 vertical team-members-card">
    <div class="row">
        @foreach($cData->company as $key=>$val)
            <div class="col-lg-4">
                <a href="/dormitory/{{$val->id}}">
                    <div class="team-member">
                        @isset($val->images)
                            @foreach($val->images as $key2=>$val2)
                                @if($val2->sort == 1)
                                    <div class="team-image">
                                        <img alt=""
                                             src="{{url("images/images/". $val2->file)}}">
                                    </div>
                                @endif
                            @endforeach
                        @endisset
                        <div class="team-desc py-3 px-3">
                            <div class="titlePrice d-flex align-items-center justify-content-between mb-3">
                                <div class="dataList-title float-left">{{$val->name}}</div>
                                <div class="price-amount"><span>2,700â‚º</span><span
                                        class="after-span">/month</span></div>
                            </div>
                            <div class="row ml-0">
                                <button class="datalist-button">Air Conditioning</button>
                                <button class="datalist-button">Free Wi-Fi</button>
                                <button class="datalist-button">24/7 Assistance</button>
                                <button class="datalist-button">Swimming Pool</button>
                                <button class="datalist-button">Bills Included</button>
                            </div>
